import { Component, OnInit, OnDestroy, ViewChild, ElementRef } from '@angular/core';
import { Router } from '@angular/router';
import { IonicModule } from '@ionic/angular';
import { CommonModule } from '@angular/common';
import { CallService } from '../../services/call.service';
import { CallControlsComponent } from '../../components/call-controls/call-controls.component';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-call',
  templateUrl: './call.page.html',
  styleUrls: ['./call.page.scss'],
  standalone: true,
  imports: [IonicModule, CommonModule, CallControlsComponent]
})
export class CallPage implements OnInit, OnDestroy {
  @ViewChild('localVideo') localVideo!: ElementRef<HTMLVideoElement>;
  @ViewChild('remoteVideo') remoteVideo!: ElementRef<HTMLVideoElement>;

  recipientId: string = '';
  callDuration: string = '00:00';
  callState$: Observable<any>;
  private durationInterval: any;
  private startTime: number = 0;

  constructor(
    private callService: CallService,
    private router: Router
  ) {
    const navigation = this.router.getCurrentNavigation();
    if (navigation?.extras.state) {
      this.recipientId = navigation.extras.state['recipientId'];
    }
    this.callState$ = this.callService.getCallState();
  }

  ngOnInit() {
    this.startTime = Date.now();
    this.updateCallDuration();
    this.durationInterval = setInterval(() => this.updateCallDuration(), 1000);
  }

  ngOnDestroy() {
    if (this.durationInterval) {
      clearInterval(this.durationInterval);
    }
    this.callService.endCall();
  }

  private updateCallDuration() {
    const duration = Math.floor((Date.now() - this.startTime) / 1000);
    const minutes = Math.floor(duration / 60);
    const seconds = duration % 60;
    this.callDuration = `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
  }

  async endCall() {
    try {
      await this.callService.endCall();
      this.router.navigate(['/home']);
    } catch (error) {
      console.error('Failed to end call:', error);
    }
  }
}
